const password = msg.payload.password;

// Validación de campos requeridos
if (!identifier || !password || typeof password !== 'string' || password.length === 0) {
  msg.statusCode = 400;
  msg.payload = {
    success: false,
    message: 'Email/usuario/teléfono y contraseña son requeridos'
  };
  return msg;
}

// Guardamos password en msg para verificarlo después
msg.passwordToVerify = password;

