return msg;

try {
  const result = msg.payload;
  const passwordFromRequest = msg.passwordToVerify;

  // Verificar si el usuario existe
  if (!Array.isArray(result) || result.length === 0) {
    msg.statusCode = 401;
    msg.payload = {
      success: false,
      message: 'Email o contraseña incorrectos'
    };
    return msg;
  }

  const user = result[0];

  // Comparar contraseña (en prototipo es comparación directa)
  // En producción: usar bcrypt.compare()
  if (user.password !== passwordFromRequest) {
    msg.statusCode = 401;
    msg.payload = {
      success: false,
      message: 'Email o contraseña incorrectos'
    };
    return msg;
  }

  // ÉXITO: Credentials válidas, devolver role
  msg.statusCode = 200;
  msg.payload = {
    success: true,
    user_id: user.id,
    name: user.full_name,
    email: user.email, // Necesario para notificación de login
    role: user.role, // 'worker' o 'refugee'
    token: 'simulated_token_' + Date.now() // Token simulado
  };

  // Limpiar datos sensibles
  delete msg.passwordToVerify;

  return msg;

} catch (err) {
  msg.statusCode = 500;
  msg.payload = {
    success: false,
    message: 'Error procesando login'
  };
  return msg;
}

// 1. Extraemos los datos que vienen del formulario (Frontend)
var nombre = msg.payload.name;
var email = msg.payload.email;
var password = msg.payload.password;

